export default class  Game extends Component { 

// adding a constructor to  the Game  initial state to contain an array

    render(){ 
            return( 
              <React.Fragment> 
                {/* Name of the players */}
                   <div className="row">
                      <div className="col-sm-6">

                     {/* <Header handleSubmit={fields => this.handleSubmit(fields)} />
                    <p>{JSON.stringify(this.state.fields)}</p> */}

                      <p className="NameX">X</p>

                      </div>
                      <div className="col-sm-6">
                        {/* <Header handleSubmit={fields => this.handleSubmit(fields)} />
                        <p>{JSON.stringify(this.state.fields)}</p> */}

                        <p className="Name0">0</p>

                        </div>
                      </div>
                      <div className="row">
                      <div className="col-sm-6">
                          {/* Board of the Game  */}
                      <main>
                        <article>
                        <div>
                        <Board/>
                        </div> 
                        </article>
                    </main>
                      </div>
                      </div>
                      <div className="row">
                        <div className="col-md-6 margin-left ">
                          <button>New Game</button>
                        </div>
                        <div className="col-md-6">
                          <button>Reset</button>
                        </div>
                      </div>


              </React.Fragment>  
                  );
  }
}
