                      <div className="row">
                      <div className="col-sm-6">
                          {/* Board of the Game  */}
                      <main>
                        <article>
                        <div>
                        <Board/>
                        </div> 
                        </article>
                    </main>
                      </div>
                      </div>
                      <hr/>
                      <div className="d-flex bd-highlight mb-3 ">
                          <div className=" order-3 p-2 bd-highlight">
                               <button>New Game</button>
                          </div>
                          <div className="p-2 bd-highlight"> 
                                 <button>Reset</button>
                          </div>
                       </div>    
              </React.Fragment>  
                  );
  }
}
